import React, { memo } from 'react';
import PropTypes from 'prop-types';
import { HelperStyle, LinearGradient, View, Validate } from '../../index';
import Style from './style';

/**
 * Componente para mostrar dos contenidos uno con color de fondo blanco (parte inferior) y otro el gradiente (parte superior).
 *
 * @component
 */
const ContentGradient = ({ renderContentGradient, renderContent }) => (
    <>
        <LinearGradient
            start={{ x: 0, y: 0 }}
            end={{ x: 1, y: 0 }}
            locations={[0.0, 0.99]}
            style={HelperStyle.pb_4}
        >
            {Validate.render(renderContentGradient)}
        </LinearGradient>
        <View
            style={[
                HelperStyle.bg_white,
                HelperStyle.flx_i,
                HelperStyle.bg_white,
                HelperStyle.btlr_3,
                HelperStyle.btrr_3,
                HelperStyle.of_h,
                Style.styleContent,
            ]}
        >
            {Validate.render(renderContent)}
        </View>
    </>
);

ContentGradient.propTypes = {
    /**
     * Componente a renderizar en el area de contenido con gradiente.
     */
    renderContentGradient: PropTypes.oneOfType([
        PropTypes.func,
        PropTypes.bool,
        PropTypes.node,
    ]),
    /**
     * Componente a renderizar en el area de contenido con fondo blanco.
     */
    renderContent: PropTypes.oneOfType([
        PropTypes.func,
        PropTypes.bool,
        PropTypes.node,
    ]),
};
ContentGradient.defaultProps = {
    renderContentGradient: false,
    renderContent: false,
};

export default memo(ContentGradient);
