import React, { memo } from 'react';
import DropdownAlert from 'react-native-dropdownalert';
import Images from 'bch-assets';
import { HelperStyle } from '../../index';
import DropDownHolder from './DropDownHolder';
import Style from './style';

const emotiSuccess = Images.alerts.success;
const emotiInfo = Images.alerts.info;
const emotiWarning = Images.alerts.warning;
const emotiError = Images.alerts.error;

/**
 * Componente para mostrar mensajes de tipo notificaciÃ³n. Extiende de react-native-dropdownalert.
 *
 * @component
 */
const DropdownAlertBCH = (props) => (
    <DropdownAlert
        {...props}
        ref={(ref) => DropDownHolder.setDropDown(ref)}
        imageStyle={Style.imageStyle}
        successImageSrc={emotiSuccess}
        infoImageSrc={emotiInfo}
        warnImageSrc={emotiWarning}
        errorImageSrc={emotiError}
        successColor={HelperStyle.success.color}
        infoColor={HelperStyle.info.color}
        warnColor={HelperStyle.warning.color}
        errorColor={HelperStyle.danger.color}
        translucent
        updateStatusBar={false}
        useNativeDriver
    />
);

export default memo(DropdownAlertBCH);
