import React, { memo } from 'react';
import PropTypes from 'prop-types';
import { Text, HelperStyle } from '../../index';
import Style from './style';

/**
 * Componente para mostrar texto tipo título.
 *
 * @component
 */
const H4 = (props) => {
    const { children, style, headerTitle } = props;
    return (
        <Text
            {...props}
            style={[
                Style.h4,
                HelperStyle.gray_dark,
                headerTitle && Style.headerTitle,
                style,
            ]}
        >
            {children}
        </Text>
    );
};

H4.propTypes = {
    /**
     * Texto a mostrar.
     */
    children: PropTypes.node.isRequired,
    /**
     * Estilos para título en navigation header.
     */
    headerTitle: PropTypes.bool,
    /**
     * Estilos personalizados.
     */
    style: PropTypes.oneOfType([PropTypes.object, PropTypes.array]),
};
H4.defaultProps = {
    headerTitle: false,
    style: {},
};

export default memo(H4);
