import React, { memo } from 'react';
import PropTypes from 'prop-types';
import Highlighter from 'react-native-highlight-words';
import { HelperStyle } from '../../index';
import Styles from './style';

/**
 * Componente para mostrar texto resaltado.
 *
 * @component
 */
const HighlighterBCH = (props) => {
    const { highlightStyle, style, number } = props;
    return (
        <Highlighter
            {...props}
            style={[
                Styles.TextStyle,
                HelperStyle.gray,
                number && Styles.number,
                style,
            ]}
            highlightStyle={[
                HelperStyle.ff_fontFamilyBold,
                HelperStyle.primary,
                highlightStyle,
            ]}
        />
    );
};

HighlighterBCH.propTypes = {
    /**
     * Estilos para texto resaltado.
     */
    highlightStyle: PropTypes.oneOfType([PropTypes.object, PropTypes.array]),
    /**
     * Estilos para texto.
     */
    style: PropTypes.oneOfType([PropTypes.object, PropTypes.array]),
    /**
     * Estilos para textos tipo num√©rico.
     */
    number: PropTypes.bool,
};
HighlighterBCH.defaultProps = {
    highlightStyle: {},
    style: {},
    number: false,
};

export default memo(HighlighterBCH);
