import React, { memo } from 'react';
import PropTypes from 'prop-types';
import { Icon } from 'native-base';
import { HelperStyle } from '../../index';
import IconCustom from './CustomIcon';

/**
 * Componente para mostrar un ícono.
 *
 * @component
 */
const IconBCH = (props) => {
    const { style, type, name, ...iconProps } = props;
    if (type) {
        return (
            <Icon
                {...iconProps}
                type={type}
                name={name}
                style={[HelperStyle.gray_dark, HelperStyle.fs_5, style]}
            />
        );
    }
    return (
        <IconCustom
            {...iconProps}
            name={name}
            style={[HelperStyle.gray_dark, HelperStyle.fs_5, style]}
        />
    );
};

IconBCH.propTypes = {
    /**
     * El nomnbre del ícono a mostrar.
     */
    name: PropTypes.string,
    /**
     * Familia tipográfica del ícono.
     */
    type: PropTypes.string,
    /**
     * Estilos para el ícono.
     */
    style: PropTypes.oneOfType([PropTypes.object, PropTypes.array]),
};
IconBCH.defaultProps = {
    name: '',
    type: '',
    style: {},
};

export default memo(IconBCH);
