import React, { memo } from 'react';
import PropTypes from 'prop-types';
import { HelperStyle, Icon } from '../../index';
import Styles from './style';

/**
 * Componente puntos.
 *
 * @component
 */
const IconCircleFill = ({ length, style }) =>
    Array(...Array(length)).map((_, idx) => {
        const key = `iconfill-${idx}`;
        const islastItem = idx === length - 1;
        return (
            <Icon
                key={key}
                name="icon-bch-circle-fill"
                style={[
                    Styles.iconStyle,
                    HelperStyle.as_c,
                    islastItem && HelperStyle.mr_0,
                    HelperStyle.gray,
                    style,
                ]}
            />
        );
    });

IconCircleFill.propTypes = {
    /**
     * Numero de puntos.
     */
    length: PropTypes.number,
    /**
     * Estilos.
     */
    style: PropTypes.oneOfType([PropTypes.object, PropTypes.array]),
};
IconCircleFill.defaultProps = {
    length: 4,
    style: {},
};

export default memo(IconCircleFill);
