import React from 'react';
import { ActivityIndicator } from 'react-native';
import { HelperStyle, Spacing } from '../../../index';
import { inputPropTypes, inputDefaultProps } from '../type';
import INPUT_STR from '../inputStr';

const InputLoading = ({ inverse }) => {
    const getColor = inverse
        ? HelperStyle.white.color
        : HelperStyle.primary.color;
    return (
        <ActivityIndicator
            accessibilityLabel={INPUT_STR.InputLoading.accessibilityLabel}
            accessibilityHint={INPUT_STR.InputLoading.accessibilityHint}
            testID={INPUT_STR.InputLoading.testID}
            color={getColor}
            size={Spacing.type_5}
        />
    );
};

InputLoading.propTypes = {
    inverse: inputPropTypes.inverse,
};
InputLoading.defaultProps = {
    inverse: inputDefaultProps.inverse,
};

export default InputLoading;
