import { StyleSheet } from 'react-native';
import { HelperStyle } from '../../index';

const Style = ({ firstItem, lastItem, middleItem }) => {
    const isOnlyItem = !firstItem && !lastItem && !middleItem;
    return StyleSheet.create({
        boxStyle: {
            ...HelperStyle.ph_4,
            ...HelperStyle.pb_2,
            ...HelperStyle.pt_2,
            ...HelperStyle.bg_white,
            ...HelperStyle.br_3,
            ...HelperStyle.mb_0,
            ...(isOnlyItem && HelperStyle.pt_4),
            ...(firstItem && {
                ...HelperStyle.bblr_0,
                ...HelperStyle.bbrr_0,
                ...HelperStyle.pt_4,
            }),
            ...(lastItem && {
                ...HelperStyle.btlr_0,
                ...HelperStyle.btrr_0,
                ...HelperStyle.pb_4,
            }),
            ...(middleItem && HelperStyle.br_0),
        },
    });
};

export default Style;
