import React, { memo } from 'react';
import { HelperStyle, Icon } from '../../../index';
import buildStyles from '../style';
import KEYBOARDVIEW_STR from '../KeyBoardViewStr';
import {
    KeyBoardIconPropTypes,
    KeyBoardIconDefaultProps,
    keyBoardDefaultProps,
    keyBoardPropTypes,
} from '../type';

/**
 * Retorna componente icono biometrics o backspace del teclado.
 *
 * @component
 */
const KeyBoardIcon = ({ type, biometricAvailable, iconBiometricsName }) => {
    const Styles = buildStyles({ HelperStyle });
    if (type === 'biometrics' && biometricAvailable) {
        return (
            <Icon
                style={Styles.BotonesNumericos}
                name={iconBiometricsName}
                accessible
                accessibilityLabel={
                    KEYBOARDVIEW_STR.iconBiometrics.accessibilityLabel
                }
                accessibilityHint={
                    KEYBOARDVIEW_STR.iconBiometrics.accessibilityHint
                }
                testID={KEYBOARDVIEW_STR.iconBiometrics.testID}
            />
        );
    }
    return (
        <Icon
            style={Styles.BotonesNumericos}
            name={KEYBOARDVIEW_STR.iconDelete.iconName}
            accessible
            accessibilityLabel={KEYBOARDVIEW_STR.iconDelete.accessibilityLabel}
            accessibilityHint={KEYBOARDVIEW_STR.iconDelete.accessibilityHint}
            testID={KEYBOARDVIEW_STR.iconDelete.testID}
        />
    );
};

KeyBoardIcon.propTypes = {
    type: KeyBoardIconPropTypes.type,
    biometricAvailable: keyBoardPropTypes.biometricAvailable,
    iconBiometricsName: keyBoardPropTypes.iconBiometricsName,
};
KeyBoardIcon.defaultProps = {
    type: KeyBoardIconDefaultProps.type,
    biometricAvailable: keyBoardDefaultProps.biometricAvailable,
    iconBiometricsName: keyBoardDefaultProps.iconBiometricsName,
};

export default memo(KeyBoardIcon);
