import React, { memo } from 'react';
import PropTypes from 'prop-types';
import { View } from 'native-base';
import Style from './style';

/**
 * Componente contenedor que establece diferentes tama√±os a un componente <LottieView />.
 *
 * @component
 */
const LottieViewContainer = (props) => {
    const { children, extraLarge, large, small, style } = props;
    return (
        <View
            {...props}
            style={[
                Style.LottieViewContainer,
                small && Style.small,
                large && Style.large,
                extraLarge && Style.extraLarge,
                style,
            ]}
        >
            {children}
        </View>
    );
};

LottieViewContainer.propTypes = {
    /**
     * Componente <LottieView />.
     */
    children: PropTypes.node.isRequired,
    /**
     * Establece estilo altura extraLarge.
     */
    extraLarge: PropTypes.bool,
    /**
     * Establece estilo altura large.
     */
    large: PropTypes.bool,
    /**
     * Establece estilo altura small.
     */
    small: PropTypes.bool,
    /**
     * Establece estilo para el contenedor.
     */
    style: PropTypes.oneOfType([PropTypes.object, PropTypes.array]),
};
LottieViewContainer.defaultProps = {
    extraLarge: false,
    large: false,
    small: false,
    style: {},
};

export default memo(LottieViewContainer);
