import React, { memo } from 'react';
import PropTypes from 'prop-types';
import { Animated as AnimatedReactNative } from 'react-native';
import Animated from 'react-native-reanimated';

const AnimatedView = (props) => {
    const { reanimated, ...propsAnimatedView } = props;
    if (reanimated) {
        return <Animated.View {...propsAnimatedView} />;
    }
    return <AnimatedReactNative.View {...propsAnimatedView} />;
};

AnimatedView.propTypes = {
    reanimated: PropTypes.bool,
};
AnimatedView.defaultProps = {
    reanimated: false,
};

export default memo(AnimatedView);
