import React, { memo } from 'react';
import { isObject, has } from 'lodash';
import { HelperStyle, Paragraph } from '../../../index';
import { modalPropTypes, modalDefaultProps } from '../type';

const ModalBody = ({
    renderContent,
    content,
    lottieIcon,
    styleTextModalDark,
}) => {
    if (renderContent) return renderContent();
    if (isObject(content) && has(content, 'items')) {
        return <Paragraph {...content} />;
    }
    if (content) {
        return (
            <Paragraph
                style={[lottieIcon && HelperStyle.ta_c, styleTextModalDark]}
                items={content}
            />
        );
    }
    return null;
};

ModalBody.propTypes = {
    renderContent: modalPropTypes.renderContent,
    content: modalPropTypes.content,
    lottieIcon: modalPropTypes.lottieIcon,
    styleTextModalDark: modalPropTypes.style,
};
ModalBody.defaultProps = {
    renderContent: modalDefaultProps.renderContent,
    content: modalDefaultProps.content,
    lottieIcon: modalDefaultProps.lottieIcon,
    styleTextModalDark: modalDefaultProps.style,
};

export default memo(ModalBody);
