import React, { memo } from 'react';
import { ButtonsGroup } from '../../../index';
import { modalPropTypes, modalDefaultProps } from '../type';

const ModalFooter = ({ renderFooter, buttonsGroup }) => {
    if (renderFooter) return renderFooter();
    if (buttonsGroup) return <ButtonsGroup items={buttonsGroup} />;
    return null;
};

ModalFooter.propTypes = {
    renderFooter: modalPropTypes.renderFooter,
    buttonsGroup: modalPropTypes.buttonsGroup,
};
ModalFooter.defaultProps = {
    renderFooter: modalDefaultProps.renderFooter,
    buttonsGroup: modalDefaultProps.buttonsGroup,
};

export default memo(ModalFooter);
