import React, { memo } from 'react';
import PropTypes from 'prop-types';
import { View } from 'native-base';
import { HelperStyle } from '../../../index';
import { modalPropTypes, modalDefaultProps } from '../type';
import Style from '../style';

const ModalTop = ({ renderTop, isModalDarkTheme }) => {
    if (renderTop) return renderTop();
    return (
        <View
            style={[
                Style.modalCloseBar,
                isModalDarkTheme
                    ? HelperStyle.bg_gray
                    : HelperStyle.bg_gray_background,
            ]}
        />
    );
};

ModalTop.propTypes = {
    renderTop: modalPropTypes.renderTop,
    isModalDarkTheme: PropTypes.bool.isRequired,
};
ModalTop.defaultProps = {
    renderTop: modalDefaultProps.renderTop,
};

export default memo(ModalTop);
