import React from 'react';
import { Pressable } from 'react-native';
import { HelperStyle } from '../../index';
import { propTypes, defaultProps } from './type';
import buildStyle from './style';

/**
 * Componente para mostrar una caja accionable.
 *
 * @component
 */
const PressableOpacity = ({
    children,
    delayLongPress,
    disabled,
    hitSlop,
    onLongPress,
    onPress,
    onPressIn,
    onPressOut,
    pressRetentionOffset,
    style,
    testID,
    accessibilityLabel,
    accessibilityHint,
}) => {
    const Style = ({ pressed }) => buildStyle({ pressed, HelperStyle, style });
    return (
        <Pressable
            delayLongPress={delayLongPress}
            disabled={disabled}
            hitSlop={hitSlop}
            onLongPress={onLongPress}
            onPress={onPress}
            onPressIn={onPressIn}
            onPressOut={onPressOut}
            pressRetentionOffset={pressRetentionOffset}
            testID={testID}
            accessibilityLabel={accessibilityLabel}
            accessibilityHint={accessibilityHint}
            style={Style}
        >
            {children}
        </Pressable>
    );
};

PressableOpacity.propTypes = propTypes;

PressableOpacity.defaultProps = defaultProps;

export default PressableOpacity;
