import React from 'react';
import PropTypes from 'prop-types';
import { HelperStyle, View } from '../../../index';
import buildStyle from '../style';

const Step = ({ style }) => {
    const styles = buildStyle(HelperStyle);
    return <View style={[styles.step, HelperStyle.bg_gray_lighter, style]} />;
};

Step.propTypes = {
    /**
     * Estilo del paso.
     */
    style: PropTypes.oneOfType([PropTypes.object, PropTypes.array]),
};
Step.defaultProps = {
    style: HelperStyle.bg_gray_lighter,
};

export default Step;
