import React, { memo } from 'react';
import { View } from 'native-base';
import SubTabsHeader from './SubTabsHeader';
import { Validate } from '../../index';
import {
    subTabsHeaderPropTypes,
    subTabsHeaderDefaultProps,
    subTabsPropTypes,
    subTabsDefaultProps,
} from './type';

/**
 * Componente Tabs.
 *
 * @component
 */
const SubTabs = ({
    selected,
    itemActive,
    buttonsHeader,
    renderContentSegment,
    testID,
}) => (
    <View>
        <SubTabsHeader
            selected={selected}
            itemActive={itemActive}
            buttonsHeader={buttonsHeader}
            testID={testID}
        />
        {Validate.render(renderContentSegment)}
    </View>
);

SubTabs.propTypes = {
    selected: subTabsHeaderPropTypes.selected,
    itemActive: subTabsHeaderPropTypes.itemActive,
    buttonsHeader: subTabsHeaderPropTypes.buttonsHeader,
    testID: subTabsHeaderPropTypes.testID,
    renderContentSegment: subTabsPropTypes.renderContentSegment,
};
SubTabs.defaultProps = {
    selected: subTabsHeaderDefaultProps.selected,
    itemActive: subTabsHeaderDefaultProps.itemActive,
    buttonsHeader: subTabsHeaderDefaultProps.buttonsHeader,
    testID: subTabsHeaderDefaultProps.testID,
    renderContentSegment: subTabsDefaultProps.renderContentSegment,
};

export default memo(SubTabs);
