import { StyleSheet } from 'react-native';
import { Scale } from '../../index';

const Style = (props) => {
    const { first, last, HelperStyle } = props;
    const firstStyle = first && {
        ...HelperStyle.btlr_3,
        ...HelperStyle.bblr_3,
    };
    const lastStyle = last && { ...HelperStyle.btrr_3, ...HelperStyle.bbrr_3 };

    return StyleSheet.create({
        small: {
            ...firstStyle,
            ...lastStyle,
            height: Scale(32),
        },
        scroll: {
            ...HelperStyle.pl_4,
            ...HelperStyle.pr_4,
        },
    });
};

export default Style;
