import React, { memo } from 'react';
import { HelperStyle, Text, Validate } from '../../index';
import { SwitchpropTypes, SwitchdefaultProps } from './type';

/**
 * Componente para mostrar texto en el switch component.
 *
 * @component
 */
const SwitchLabel = ({ renderLabel, label }) => {
    if (renderLabel) return Validate.render(renderLabel);
    return (
        <Text small style={HelperStyle.mb_0}>
            {label}
        </Text>
    );
};

SwitchLabel.propTypes = {
    label: SwitchpropTypes.label,
    renderLabel: SwitchpropTypes.renderLabel,
};
SwitchLabel.defaultProps = {
    label: SwitchdefaultProps.label,
    renderLabel: SwitchdefaultProps.renderLabel,
};

export default memo(SwitchLabel);
