import { StyleSheet } from 'react-native';
import { Platform } from '../../index';

const switchWidth = Platform.android ? 47 : 51;

const Style = (HelperStyle, labelRight) =>
    StyleSheet.create({
        colSwitch: {
            width: switchWidth,
            justifyContent: 'center',
        },
        containerSwitch: {
            width: switchWidth,
        },
        containerloading: {
            position: 'absolute',
            width: switchWidth,
            ...(Platform.android && !labelRight
                ? HelperStyle.as_fe
                : HelperStyle.as_fs),
        },
        containerSwitchWithLabel: {
            ...HelperStyle.jc_sb,
            ...HelperStyle.ai_c,
            ...HelperStyle.mb_4,
            flexDirection: labelRight ? 'row-reverse' : 'row',
        },
        containerLabel: {
            ...HelperStyle.flx_i,
            ...(labelRight ? HelperStyle.pl_2 : HelperStyle.pr_2),
        },
    });
export default Style;
