import { useHeaderHeight } from '@react-navigation/elements';

/**
 * Wrapper del hook useHeaderHeight para obtener estilos con la altura del header del stack de navegacion.
 *
 * Condiciones para ser usada:
 * 1. Debe ser usada dentro del cuerpo de un componente funcional.
 * 2. El componente funcional debe ser hijo o estar dentro de un stackNavigation.
 *
 * @returns {object} - Objecto con estilos con la altura del header.
 */
const useHeaderHeightStyle = () => {
    const height = useHeaderHeight();
    return {
        pb: { paddingBottom: height },
        pt: { paddingTop: height },
        mt: { marginTop: height },
        mb: { marginBottom: height },
        height,
    };
};

export default useHeaderHeightStyle;
