import { useBottomTabBarHeight } from '@react-navigation/bottom-tabs';
import { minerva, Platform } from '../index';

/**
 * Wrapper del hook useBottomTabBarHeight para obtener estilos con la altura del tabBar del BottomTabNavigator.
 *
 * Condiciones para ser usada:
 * 1. Debe ser usada dentro del cuerpo de un componente funcional.
 * 2. El componente funcional debe ser hijo o estar dentro de un BottomTabNavigator.
 *
 * @returns {object} - Objecto con estilos con la altura del tabBar.
 */
const useTabBarHeightStyle = () => {
    const { footerHeight } = minerva();
    const bottomTabBarHeight = useBottomTabBarHeight();
    const heightIOS =
        bottomTabBarHeight > footerHeight ? bottomTabBarHeight : footerHeight;
    const height = Platform.android ? footerHeight : heightIOS;
    return {
        pb: { paddingBottom: height },
        pt: { paddingTop: height },
        mt: { marginTop: height },
        mb: { marginBottom: height },
        height,
    };
};

export default useTabBarHeightStyle;
