/**
 * Retornar la última propiedad verdadera de un objecto.
 *
 * @param {object} props - Las propiedades del componente.
 * @param {Array} whichProp - Arreglo de las propidades a tomar en cuenta en el filtrado.
 * @returns {string} - Retorna la clave de la última propiedad verdadera. Si no encuentra retorna false.
 */
const getActiveProp = (props, whichProp) =>
    Object.keys(props)
        .reverse()
        .find((i) => {
            if (whichProp.includes(i)) {
                return props[i] === true;
            }
            return false;
        });

export default getActiveProp;
