import { forEach, toPairs } from 'lodash';

const styleProperties = {
    width: {
        w: 'Width',
        tw: 'TopWidth',
        rw: 'RightWidth',
        bw: 'BottomWidth',
        lw: 'LeftWidth',
    },
    radius: {
        r: 'Radius',
        tlr: 'TopLeftRadius',
        trr: 'TopRightRadius',
        blr: 'BottomLeftRadius',
        brr: 'BottomRightRadius',
    },
};

const buildStyle = (scale, properties) => {
    const where = toPairs(properties);
    const style = {};
    forEach(where, ([whereShort, whereLong]) => {
        forEach(scale, (v, i) => {
            style[`b${whereShort}${i}`] = {
                [`border${whereLong}`]: v,
            };
        });
    });
    return style;
};

export const width = (scale) => buildStyle(scale, styleProperties.width);

export const radius = (scale) => buildStyle(scale, styleProperties.radius);
