import { forEach } from 'lodash';
import { Dimensions } from 'react-native';

const buildHeight = () => {
    const style = {
        h_auto: {
            height: 'auto',
        },
        h_window: {
            height: Dimensions.get('window').height,
        },
        h_0: {
            height: 0,
        },
    };
    const sizes = ['100', '90', '80', '70', '60', '50', '40', '30', '20', '10'];
    forEach(sizes, (v) => {
        style[`h_${v}`] = {
            height: `${v}%`,
        };
        style[`minh_${v}`] = {
            minHeight: `${v}%`,
        };
        style[`maxh_${v}`] = {
            maxHeight: `${v}%`,
        };
    });
    return style;
};
const heights = buildHeight();

export default heights;
