export default (colors) => {
    const defaultStyle = {
        shadowColor: colors.brand,
        shadowOpacity: 0.1,
        shadowRadius: 4,
    };
    return {
        shadow: {
            ...defaultStyle,
            shadowOffset: { width: 0, height: 2 },
            elevation: 2,
        },
        shadow_top: {
            ...defaultStyle,
            shadowOffset: { width: 0, height: -4 },
            elevation: 10,
        },
        shadow_disabled: {
            shadowOpacity: 1,
            shadowRadius: 1,
            shadowOffset: { width: 0, height: 0 },
            elevation: 1,
        },
        shadow_header: {
            ...defaultStyle,
            shadowOffset: { width: 0, height: 4 },
            elevation: 10,
            backgroundColor: '#fff',
            borderBottomWidth: 0,
            borderBottomColor: '#fff',
        },
        shadow_none: {
            elevation: 0,
            shadowRadius: 0,
            shadowOpacity: 0,
        },
    };
};
