import { forEach } from 'lodash';

export default ({ type, lineHeight }) => {
    const style = {
        lh_none: {
            lineHeight: null,
        },
    };

    // FontSize
    forEach(type, (scale, idx) => {
        style[`fs${idx}`] = {
            fontSize: scale,
        };
    });

    // LineHeight
    forEach(lineHeight, (scale, idx) => {
        style[`lh${idx}`] = {
            lineHeight: scale,
        };
    });

    return style;
};
