import { forEach } from 'lodash';
import { Dimensions } from 'react-native';

const buildWidth = () => {
    const style = {
        w_auto: {
            width: 'auto',
        },
        w_window: {
            width: Dimensions.get('window').width,
        },
        w_0: {
            width: 0,
        },
    };
    const sizes = ['100', '90', '80', '70', '60', '50', '40', '30', '20', '10'];
    forEach(sizes, (v) => {
        style[`w_${v}`] = {
            width: `${v}%`,
        };
        style[`minw_${v}`] = {
            minWidth: `${v}%`,
        };
        style[`maxw_${v}`] = {
            maxWidth: `${v}%`,
        };
    });
    return style;
};
const widths = buildWidth();

export default widths;
