import {
    Platform,
    LayoutAnimation as LayoutAnimationRN,
    UIManager,
} from 'react-native';

if (
    Platform.OS === 'android' &&
    UIManager.setLayoutAnimationEnabledExperimental
) {
    UIManager.setLayoutAnimationEnabledExperimental(true);
}

const LayoutAnimation = (duration = 600) => {
    const CONFIG = {
        duration,
        create: {
            type: LayoutAnimationRN.Types.linear,
            property: LayoutAnimationRN.Properties.opacity,
        },
        update: {
            type: LayoutAnimationRN.Types.easeInEaseOut,
        },
        delete: {
            type: LayoutAnimationRN.Types.linear,
            property: LayoutAnimationRN.Properties.opacity,
        },
    };
    LayoutAnimationRN.configureNext(CONFIG, this.onAnimationDidEnd);
};

export default LayoutAnimation;
